mapper.select:
select id_cita,codigo_cita,tipo_reserva,fecha_atencion, CONVERT(TIME(0), hora_atencion) hora_atencion, c.id_hc,h.nom_paciente,h.apellido_paterno_paciente, h.apellido_materno_paciente, c.id_consultorio,cn.cod_consultorio from cita c inner join HISTORIA_CLINICA h on c.id_hc = c.id_hc inner join CONSULTORIO cn on c.id_consultorio = cn.id_consultorio where estado_cita = 'RESERVA'
and h.nom_paciente like CONCAT(#{nombre},'%')
AND h.dni_paciente = #{dni}
AND codigo_cita = #{codigoCita}

mapper.update:
UPDATE CITA set tipo_reserva = #{tipoReserva}, id_consultorio = #{consultorio.id}, fecha_atencion = #{fechaAtencion},
hora_atencion = #{horaAtencion}, monto_pago = #{monto}
where id_cita = #{id}
